import React from 'react';
import './index.css';
import { Calendar, Code, Users } from 'lucide-react';

const Header = () => (
  <header className="bg-gray-900 text-white p-4">
    <div className="container mx-auto flex justify-between items-center">
      <h1 className="text-2xl font-bold">US Hackathon 2024</h1>
      <nav>
        <ul className="flex space-x-4">
          <li><a href="#about" className="hover:text-gray-300">About</a></li>
          <li><a href="#schedule" className="hover:text-gray-300">Schedule</a></li>
          <li><a href="#sponsors" className="hover:text-gray-300">Sponsors</a></li>
          <li><a href="#contact" className="hover:text-gray-300">Contact</a></li>
        </ul>
      </nav>
    </div>
  </header>
);

const Hero = () => (
  <section className="bg-blue-600 text-white py-20">
    <div className="container mx-auto text-center">
      <h2 className="text-5xl font-bold mb-4">US Hackathon 2024</h2>
      <p className="text-xl mb-8">Join us for 48 hours of innovation, collaboration, and coding!</p>
      <a href="#" className="bg-white text-blue-600 px-6 py-3 rounded-full font-bold text-lg hover:bg-gray-100 transition duration-300">Register Now</a>
    </div>
  </section>
);

const FeatureCard = ({ icon: Icon, title, description }) => (
  <div className="bg-white p-6 rounded-lg shadow-md">
    <Icon className="text-blue-600 w-12 h-12 mb-4" />
    <h3 className="text-xl font-semibold mb-2">{title}</h3>
    <p className="text-gray-600">{description}</p>
  </div>
);

const Features = () => (
  <section className="py-16 bg-gray-100">
    <div className="container mx-auto">
      <h2 className="text-3xl font-bold text-center mb-12">Why Participate?</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        <FeatureCard 
          icon={Code}
          title="Learn & Build"
          description="Gain hands-on experience with cutting-edge technologies and build innovative projects."
        />
        <FeatureCard 
          icon={Users}
          title="Network"
          description="Connect with like-minded individuals, industry experts, and potential employers."
        />
        <FeatureCard 
          icon={Calendar}
          title="Win Prizes"
          description="Showcase your skills and win amazing prizes, including internships and job opportunities."
        />
      </div>
    </div>
  </section>
);

const CallToAction = () => (
  <section className="bg-blue-600 text-white py-16">
    <div className="container mx-auto text-center">
      <h2 className="text-3xl font-bold mb-4">Ready to hack?</h2>
      <p className="text-xl mb-8">Join us for an unforgettable experience of innovation and collaboration.</p>
      <a href="#" className="bg-white text-blue-600 px-6 py-3 rounded-full font-bold text-lg hover:bg-gray-100 transition duration-300">Register Now</a>
    </div>
  </section>
);

const Footer = () => (
  <footer className="bg-gray-900 text-white py-8">
    <div className="container mx-auto text-center">
      <p>&copy; 2024 US Hackathon. All rights reserved.</p>
    </div>
  </footer>
);

const App = () => {
  return (
    <div className="min-h-screen flex flex-col">
      <Header />
      <main className="flex-grow">
        <Hero />
        <Features />
        <CallToAction />
      </main>
      <Footer />
    </div>
  );
};

export default App;
